-- MedConnect - Fully Qualified DB Script-- Import this at the SERVER level in phpMyAdmin (no DB selection needed)-- Create databaseCREATE DATABASE IF NOT EXISTS `MedConnect`;-- Admin login tableCREATE TABLE IF NOT EXISTS `MedConnect`.`adminlogin` (    `Name` VARCHAR(255) NOT NULL,    `Email` VARCHAR(255) NOT NULL,    `Password` VARCHAR(255) NOT NULL,    PRIMARY KEY (`Name`, `Email`));-- User registration tableCREATE TABLE IF NOT EXISTS `MedConnect`.`userregistration` (    `Name` VARCHAR(255) NOT NULL,    `Email` VARCHAR(255) NOT NULL,    `Password` VARCHAR(255) NOT NULL,    `Gender` VARCHAR(10) NOT NULL,    `Date of Birth` DATE NOT NULL,    `Contact Number` VARCHAR(20) NOT NULL,    `Location` VARCHAR(255) NOT NULL,    PRIMARY KEY (`Name`, `Email`));-- NGO registration tableCREATE TABLE IF NOT EXISTS `MedConnect`.`ngoregistrationform` (    `User Name` VARCHAR(255) NOT NULL,    `Email` VARCHAR(255) NOT NULL,    `Password` VARCHAR(255) NOT NULL,    `Confirm Password` VARCHAR(255) NOT NULL,    PRIMARY KEY (`User Name`, `Email`));-- Executive tableCREATE TABLE IF NOT EXISTS `MedConnect`.`executive` (    `Name` VARCHAR(255) NOT NULL,    `Email` VARCHAR(255) NOT NULL,    `Password` VARCHAR(255) NOT NULL,    `Gender` VARCHAR(10) NOT NULL,    `Date of Birth` DATE NOT NULL,    `Contact Number` VARCHAR(20) NOT NULL,    `Location` VARCHAR(255) NOT NULL,    PRIMARY KEY (`Name`, `Email`));-- Add NGO table (admin managed)CREATE TABLE IF NOT EXISTS `MedConnect`.`addngo` (    `ID` VARCHAR(50) NOT NULL,    `Name` VARCHAR(255) NOT NULL,    `Gender` VARCHAR(10) NOT NULL,    `Email` VARCHAR(255) NOT NULL,    `Contact Number` VARCHAR(20) NOT NULL,    `Location` VARCHAR(255) NOT NULL,    `Admin Name` VARCHAR(255) NOT NULL,    `Admin Email` VARCHAR(255) NOT NULL,    PRIMARY KEY (`ID`));-- Add Medicine table (admin managed)CREATE TABLE IF NOT EXISTS `MedConnect`.`addmedicine` (    `Medicine Name` VARCHAR(255) NOT NULL,    `Price` VARCHAR(50) NOT NULL,    `Manufacture Date` VARCHAR(50) NOT NULL,    `Expire Date` VARCHAR(50) NOT NULL,    `Admin Name` VARCHAR(255) NOT NULL,    `Admin Email` VARCHAR(255) NOT NULL,    PRIMARY KEY (`Medicine Name`));-- Buy Medicine table (executive purchases)CREATE TABLE IF NOT EXISTS `MedConnect`.`buymedicine` (    `Medicine Name` VARCHAR(255) NOT NULL,    `Price` VARCHAR(50) NOT NULL,    `Manufacture Date` VARCHAR(50) NOT NULL,    `Expiry Date` VARCHAR(50) NOT NULL,    `Executive Name` VARCHAR(255) NOT NULL,    `Executive Email` VARCHAR(255) NOT NULL,    PRIMARY KEY (`Medicine Name`, `Executive Name`, `Executive Email`));-- Donate Medicine table (user donations) - uses surrogate PK to avoid huge composite keyCREATE TABLE IF NOT EXISTS `MedConnect`.`donate medicine` (    `id` BIGINT NOT NULL AUTO_INCREMENT,    `Medicine Name` VARCHAR(255) NOT NULL,    `Manufacture Date` VARCHAR(50) NOT NULL,    `Expiry Date` VARCHAR(50) NOT NULL,    `Medicine Type` VARCHAR(50) NOT NULL,    `Number of Tablet` VARCHAR(50) NOT NULL,    `Donate Time` VARCHAR(50) NOT NULL,    `Donate Date` VARCHAR(50) NOT NULL,    `Executive Name` VARCHAR(255) NOT NULL,    `Executive Email` VARCHAR(255) NOT NULL,    `User Name` VARCHAR(255) NOT NULL,    `User Email` VARCHAR(255) NOT NULL,    `Contact Number` VARCHAR(20) NOT NULL,    `Flat No` VARCHAR(100) NOT NULL,    `Street` VARCHAR(255) NOT NULL,    `Area` VARCHAR(255) NOT NULL,    `Pin Code` VARCHAR(20) NOT NULL,    `State` VARCHAR(100) NOT NULL,    `CityTown` VARCHAR(100) NOT NULL,    PRIMARY KEY (`id`),    INDEX `idx_donate_lookup` (`Medicine Name`(191), `User Email`(191), `Donate Date`));-- Seed dataINSERT INTO `MedConnect`.`adminlogin` (`Name`, `Email`, `Password`) VALUES ('admin', 'admin@medicinedonation.com', 'admin123');INSERT INTO `MedConnect`.`addmedicine` (`Medicine Name`, `Price`, `Manufacture Date`, `Expire Date`, `Admin Name`, `Admin Email`) VALUES ('Paracetamol', '50', '01-01-2024', '01-01-2026', 'admin', 'admin@medicinedonation.com'),('Aspirin', '30', '01-02-2024', '01-02-2026', 'admin', 'ad